mapper.insert:
insert into item2(
	item_id, item_name, price, description, picture_url, picture_url2
)values(
	seq_item2.nextval, #{itemName}, #{price}, #{description}, #{pictureUrl}, #{pictureUrl2}
)

mapper.select:
select picture_url
from item2
where item_id = #{itemId}

select picture_url2
from item2
where item_id = #{itemId}
